int ksm(int x,int y) {
	int z=1;
	for (;y;y>>=1,x=(ll) x*x%P) if (y&1) z=(ll) z*x%P;
	return z;
}
void pre(int n) {
	int i;
	tm2[0]=1;
	for (i=1;i<=n;i++) tm2[i]=(tm2[i-1]*2)%P;
	Jc[0]=Jc_[0]=1;
	for (i=1;i<=n;i++) Jc[i]=(ll) Jc[i-1]*i%P;
	Jc_[n]=ksm(Jc[n],P-2);
	for (i=n-1;i;i--) Jc_[i]=(ll) Jc_[i+1]*(i+1)%P;
}
int C(int n,int m) {
	if (m>n) return 0;
	return (ll) Jc[n]*Jc_[m]%P*Jc_[n-m]%P;
}
int main() {
	scanf("%d%d",&n,&m);
	pre(n);
	if (n<=2) return puts("1"),0;
	if (m==1) return printf("%d\n",tm2[n-2]),0;
	f[1][0]=1;
	for (i=1;i+1<m;i++) {
		f[i+1][0]=(f[i+1][0]+f[i][0])%P;
		f[i+1][1]=(f[i+1][1]+P-f[i][0])%P;
		f[i+1][2]=(f[i+1][2]+f[i][0])%P;
		f[i+1][i+2]=(f[i+1][i+2]+P-f[i][0])%P;
		for (j=1;j<=i;j++) {
			f[i+1][2]=(f[i+1][2]+f[i][j])%P;
			f[i+1][j+2]=(f[i+1][j+2]+P-f[i][j])%P;
		}
		for (j=1;j<=i+1;j++) f[i+1][j]=(f[i+1][j]+f[i+1][j-1])%P;
	}
	ans=((ll) f[m-1][0]*C(n-1,m-1)+ans)%P;
	for (i=2;i<m;i++) ans=((ll) f[m-1][i]*C(n-m+i-1,n-m)+ans)%P;
	if (m<n) ans=((ll) ans*tm2[n-m-1])%P;
	printf("%d\n",ans);
}

