using namespace std;

int M(int x) { return x >= mod ? x - mod : x; }
int n, k, res;
int fsp(long long bs, int p) {
    int rt = 1;
    while (p) {
        if (p & 1) rt = bs * rt % mod;
        bs = bs * bs % mod, p >>= 1;
    }
    return rt;
}

