    for (int i = 2; i < k; ++i)
        for (int j = i; j < n; ++j) f[j] = M(f[j - 1] + f[j]);

    for (int j = k - 1; j < n; ++j) res = M(res + f[j]);
    printf("%d\n", 1ll * res * fsp(2, n - k - 1) % mod);
