const int MAX_N = 2005, MOD = 1000000007;
typedef long long i64;

int N, K;
i64 fac[MAX_N], ifac[MAX_N], inv[MAX_N], bin[MAX_N];
i64 f[MAX_N][MAX_N], sum[MAX_N];

i64 binom(int n, int m) {
  if (n < m) return 0;
  return fac[n] * ifac[m] % MOD * ifac[n - m] % MOD;
}

int main() {
  scanf("%d%d", &N, &K);
  fac[0] = ifac[0] = inv[1] = bin[0] = 1;
  for (int i = 1; i <= N; ++i) fac[i] = fac[i - 1] * i % MOD;
  for (int i = 2; i <= N; ++i) inv[i] = -(MOD / i) * inv[MOD % i] % MOD;
  for (int i = 1; i <= N; ++i) ifac[i] = ifac[i - 1] * inv[i] % MOD;
  for (int i = 1; i <= N; ++i) bin[i] = (bin[i - 1] + bin[i - 1]) % MOD;

  f[0][0] = 1;

  for (int i = 0; i <= N; ++i) {
    for (int j = 0; i + j <= N; ++j) {
      if (j > 0) f[i][j] = f[i][j - 1];
      if (i > 0) f[i][j] = (f[i][j] + sum[i + j]) % MOD;
    }
    for (int j = 0; i + j < N; ++j) {
      //s - i - 1 = j
      sum[i + j + 1] = (sum[i + j + 1] + f[i][j]) % MOD;
    }
  }

  i64 res = 0;
  for (int i = 2; i <= N; ++i) {
    if (K - 1 - N + i < 0) continue;
    res = (res + binom(i - 2, N - K - 1) * bin[N - K - 1] % MOD * f[N - i][K - 1 - N + i] % MOD) % MOD;
  }
  if (K == N) res = f[N - 1][0];
  printf("%lld\n", (res + MOD) % MOD);

  return 0;
}

