ll brute_force(int n, int k){
	deque<int> dq;
	st.clear();
	REP(i, 0, 1 << (n - 1)){
		dq.clear();
		dq.pb(1);
		REP(j, 0, n - 1){
			if (i & (1 << j)){
				dq.pb(j + 2);
			} else {
				dq.push_front(j + 2);
			}
		}

		REP(i1, 0, 1 << (n - 1)){
			ll vv = 0;
			deque<int> dq1 = dq;
			int flag = 0;
			REP(j, 0, n){
				int a;
				if (i1 & (1 << j)){
					a = dq1.front();
					dq1.pop_front();
				} else {
					a = dq1.back();
					dq1.pop_back();
				}
				if (a == 1 && j + k == n){
					flag = 1;
				}
				vv = vv * (n + 1) + a;
			}
			if (flag){
				st.insert(vv);
			}
		}
	}

	return SZ(st);
}

void solve(int n, int k){
	st2[0] = 1;
	REPN(i, 1, n){
		st2[i] = (st2[i - 1] * 2) % MOD;
	}

	c[0][0] = 1;
	REPN(i, 1, n){
		c[i][0] = 1;
		REPN(j, 1, i){
			c[i][j] = (c[i - 1][j - 1] + c[i - 1][j]) % MOD;
		}
	}

	dp[0] = 1;
	REPN(i, 1, n){
		dp[i] = 0;
		REP(j, 0, i){
			//dp[i] = (dp[i] + c[i - 1][j] * dp[j]) % MOD;
			dp[i] = (dp[i] + dp[j] * dp[i - 1 - j]) % MOD;
		}
	}

	dp_v[0][0] = 1;
	REPN(sum, 1, n){
		ll dp_sum = 0ll;
		REPN(i, 0, sum){
			int j = sum - i;
			if (j){
				dp_sum = (dp_sum + dp_v[i][j - 1]) % MOD;
			}
			dp_v[i][j] = dp_sum;
		}
	}

	/*REPN(i, 1, n){
		printf("%d %lld\n", i, dp[i]);
	}*/

	if (k == 1){
		printf("%lld\n", dp[n - 1]);
		//printf("%lld\n", brute_force(n, k));
		return;
	}

	ll ans = (st2[k - 2] * dp[n - k]) % MOD;

	//printf("#1: %lld\n", ans);

	REPN(i, k + 1, n){
		ll mul = (c[i - 2][k - 2] * st2[k - 2]) % MOD;
		ll sum = dp_v[n - i][i - k];
		/*ll sum = 0ll;
		REPN(j, i, n){
			ll cur = (dp[n - j] * c[j - k - 1][i - k - 1]) % MOD;
			printf("## %lld\n", cur);
			sum = (sum + cur) % MOD;
		}*/
		ans = (ans + mul * sum) % MOD;

		//printf("# %d %lld %lld\n", i, mul, sum);
	}

	printf("%lld\n", ans);

	//printf("%lld\n", brute_force(n, k));
}

