const MOD: i64 = 1e9 as i64 + 7;

fn main() {
    let (n, k): (usize, usize) = read();

    let mut dp: Vec<Vec<Vec<i64>>> = vec![vec![vec![0; 2]; n+1]; n+1];
    dp[n][0][0] = 1;
    for i in (1..n+1).rev() {
        for j in (0..n+1).rev() {
            for k in 0..2 {
                let base = dp[i][j][k];
                if base == 0 {
                    continue
                }
                if j >= 1 && k == 0 {
                    dp[i][j-1][0] += base;
                    dp[i][j-1][0] %= MOD;
                }

                dp[i-1][j][0] += base;
                dp[i-1][j][0] %= MOD;

                dp[i-1][j+1][1] += base;
                dp[i-1][j+1][1] %= MOD;
            }
        }
    }

    let mut ans = (dp[1][n-k][0] + dp[1][n-k][1]) % MOD;
    for x in 1..n-k {
        ans *= 2;
        ans %= MOD;
    }
    println!("{}", ans);
}

