const MOD: i64 = 1_000_000_007;

fn powmod(x: i64, mut e: i64, m: i64) -> i64 {
    let mut sum = 1;
    let mut cur = x % m;
    while e > 0 {
        if e % 2 != 0 {
            sum = sum * cur % m;
        }
        cur = cur * cur % m;
        e /= 2;
    }
    sum
}

fn make_dp_table(n: usize) -> Vec<Vec<i64>> {
    let mut dp = vec![vec![0; 2 * n]; n];
    for i in 0 .. 2 * n {
        dp[0][i] = 1;
    }
    for i in 1 .. n {
        for j in 0 .. 2 * n - i {
            dp[i][j] = (dp[i - 1][j + 1] + if j > 0 { dp[i][j - 1] } else { 0 })
                % MOD;
        }
    }
    dp
}

fn main() {
    let n = get();
    let k: usize = get::<usize>() - 1;
    let mut tot: i64 = 0;
    let dp = make_dp_table(n);
    if k < n - 1 {
        for m in n - k - 1 .. n {
            let mut tmp = dp[n - m - 1][m + 1 + k - n];
            for i in 0 .. n - k - 2 {
                tmp = tmp * (m - 1 - i) as i64 % MOD;
                tmp = tmp * powmod(i as i64 + 1, MOD - 2, MOD) % MOD; 
            }
            tot += tmp;
            tot %= MOD;
        }
    } else {
        tot = dp[n - 1][0];
    }
    for _ in k + 2 .. n {
        tot = tot * 2 % MOD;
    }
    println!("{}", tot);
}
