#include <bits/stdc++.h>
using namespace std;

typedef pair <int, int> pii;
typedef pair <int, pii> pip;

struct HASH {
	size_t operator() (const pair<int,int> &x) const {
		return hash <long long> () (((long long)x.first) ^ (((long long)x.second) << 32));
	}
};

const int MAX_N = 1e5 + 5;
int n, cnt, ans, S, ANS = 1e9 + 7, T = 10000;
bool Mark[MAX_N];
bool del[MAX_N];
int d[MAX_N];
int down[MAX_N];
int up[MAX_N];
unordered_map <pii, int, HASH> Map;

vector <pii> G[MAX_N];

void dfs1 (int v,  int p) {
	for (int i = 0; i < G[v].size(); i++)
		if (G[v][i].second && G[v][i].first != p) {
			dfs1(G[v][i].first, v);
			down[v] += 1 + down[G[v][i].first];
		}
}

void dfs2 (int v, int p, int u) {
	Mark[v] = true;
	up[v] = u;

	for (int i = 0; i < G[v].size(); i++)
		if (G[v][i].second && G[v][i].first != p)
			dfs2(G[v][i].first, v, u + down[v] - down[G[v][i].first]);
}

void findS (int v, int p) {
	for (int i = 0; i < G[v].size(); i++)
		if (G[v][i].second && G[v][i].first != p && (up[G[v][i].first] < 1 + down[G[v][i].first] || (up[G[v][i].first] == 1 + down[G[v][i].first] && rand() % 2))) {
			findS(G[v][i].first, v);
			return ;
		}

	S = v;
}

int main() {
	srand(clock());
	cin >> n;
	for (int i = 1; i < n; i++) {
		int v, u;
		cin >> v >> u;
		v--; u--;
		d[v]++;
		d[u]++;

		Map[{v, u}] = G[u].size();
		Map[{u, v}] = G[v].size();
		G[v].push_back({u, 1});
		G[u].push_back({v, 1});
	}

	for (int Q = 0; Q < T; Q++) {
		for (int i = 0; i < n; i++) {
			d[i] = G[i].size();
			for (int j = 0; j < G[i].size(); j++)
				G[i][j].second = true;
		}

		while (true) {
			ans++;
			memset(Mark, 0, sizeof Mark);
			memset(down, 0, sizeof down);
			memset(up, 0, sizeof up);
			bool change = false;

			for (int i = 0; i < n; i++)
				if (!del[i] && !Mark[i]) {
					change = true;

					dfs1(i, i);
					dfs2(i, i, 0);
					findS(i, i);
					del[S] = true;

					for (int j = 0; j < G[S].size(); j++)
						if (G[S][j].second) {
							int v = G[S][j].first;
							int ind = Map[{S, v}];

							d[v]--;
							G[v][ind].second = false;
						}
				}

			for (int i = 0; i < n; i++)
				if (!d[i])
					del[i] = true;

			if (!change)
				break;
		}

		ANS = min(ANS, ans - 1);
	}

	cout << ANS << "\n";
	return 0;
}

