void dfs(int u, int par) {
    int cnt[LOGN] = {0};
    for (auto v : gr[u])
        if (v != par) {
            dfs(v, u);
            for (int i = 0; i < LOGN; i++)
                cnt[i] += ((ans[v] >> i) & 1);
        }
    int pos = -1;
    for (int i = LOGN-1; i >= 0; i--)
        if (cnt[i] >= 2) {
            // there is a path starting in one child and ending in another
            // => there cannot be a path a - b - a where b <= a
            pos = i;
            break;
        }
    for (int i = pos+1; i < LOGN; i++)
        if (cnt[i] == 0) {
            ans[u] = (1 << i);
            for (int j = i+1; j < LOGN; j++)
                if (cnt[j]) ans[u] |= (1 << j);
            break;
        }
}

