const int NV=202020;
int N;
vector<int> E[NV];
int num[NV];

pair<int,int> dfs_center(int cur,int pre) {
	pair<int,int> res=make_pair(1<<30,-1);
	int ma=0;
	num[cur]=1;

	FORR(r,E[cur]) if(r!=pre) {
		res=min(res,dfs_center(r,cur));
		ma=max(ma,num[r]);
		num[cur]+=num[r];
	}
	return min(res,make_pair(max(ma,N-num[cur]),cur));
}

int dfs(int cur,int pre) {
	int cnt[32]={};
	int mi=-1;
	int mask=0,i;
	FORR(r,E[cur]) if(r!=pre) {
		int ret = dfs(r,cur);
		mask |= ret;
		FOR(i,30) if(mask&(1<<i)) {
			cnt[i]++;
			if(cnt[i]>=2) mi=max(mi,i);
		}
	}
	mi++;
	while(cnt[mi]) mi++;
	mask |= 1<<mi;
	mask &= ~((1<<mi)-1);
	return mask;
}

void solve() {
	int i,j,k,l,r,x,y; string s;

	cin>>N;
	FOR(i,N-1) {
		cin>>x>>y;
		E[x-1].push_back(y-1);
		E[y-1].push_back(x-1);
	}

	auto center=dfs_center(0,-1);
	x = dfs(center.second,-1);
	FOR(i,32) if(x&(1<<i)) return _P("%d\n",i);
}


