void fillingGraph()
{
	int vertex0, vertex1;
	cin>>amount;
	for(int i=1; i<=amount; ++i)
	{
		cin>>vertex0>>vertex1;
		graph[vertex0].push_back(vertex1);
		graph[vertex1].push_back(vertex0);
	}
	return;
}
void dfs(int first, int parent)
{
	int value=0;
	result.insert({first, value});
	for(int second : graph[first])
	{
		if(second==parent)
		{
			continue;
		}
		dfs(second, first);
	}
	for(int nextDot : graph[first])
	{
		if(nextDot==parent)
		{
			continue;
		}
		for(auto iterator=result.begin(); iterator!=result.end(); ++iterator)
		{
			if(((*iterator).first==nextDot) && ((*iterator).second<2))
			{
				result[first]=(++(*iterator).second);
			}
			else
			{
				result[first]=value;
			}
		}
	}
	return;
}
