	dfs(1, -1);
	int answer=0;
	for(auto iterator=result.begin(); iterator!=result.end(); ++iterator)
	{
		//cout<<(*iterator).first<<" : "<<(*iterator).second<<endl;
		if((*iterator).second==2)
		{
			++answer;
		}
	}
