int n;
vector<int> g[100000];
bool del[100000];
int sz[100000];

void csz(int v, int p) {
    sz[v] = 1;
    for (int to : g[v]) if (to != p && !del[to]) {
        csz(to, v);
        sz[v] += sz[to];
    }
}

int cd(int v) {
    csz(v, -1);
    int par = -1;
    int szv = sz[v];
    while (true) {
        bool done = true;
        for (int to : g[v]) if (to != par && !del[to] && 2 * sz[to] >= szv) {
            par = v;
            v = to;
            done = false;
            break;
        }
        if (done) break;
    }
    int res, worst, wcnt;
    par = -1;
    bool firstTime = true;
    do {
        if (firstTime) {
            firstTime = false;
        } else {
            del[v] = false;
            par = v;
            v = worst;
        }
        del[v] = true;
        res = 0;
        worst = -1;
        wcnt = 0;
        for (int to : g[v]) if (!del[to]) {
            int val = cd(to) + 1;
            if (val > res) {
                res = val;
                worst = to;
                wcnt = 1;
            } else if (val == res) {
                ++wcnt;
            }
        }
    } while (wcnt == 1 && worst != par);
    del[v] = false;
    return res;
}

