int N;
vector<int> G[100000];
bool used[100000];
int sz[100000];
void dfs(int x, int p) {
  sz[x] = 1;
  for (int t : G[x]) if (t != p && !used[t]) dfs(t, x), sz[x] += sz[t];
}
P dfs2(int x, int p, int all) {
  int mc = all-sz[x];
  P m = P(INF, -1);
  for (int t : G[x]) if (t != p && !used[t]) {
    m = min(m, dfs2(t, x, all));
    mc = max(mc, sz[t]);
  }
  return min(m, P(mc, x));
}
int find_centroid(int s) {
  dfs(s, -1);
  return dfs2(s, -1, sz[s])._2;
}

int solve(int s) {
  int g = find_centroid(s);
  used[g] = true;
  int m = -1;
  for (int t : G[g]) if (!used[t]) m = max(m, solve(t));
  return m+1;
}

