vi g[N], f[N];

namespace centroid{
    int sz[N];
    bool used[N];

    void dfs(int v, int pr){
        sz[v]=1;
        for(auto to:g[v]){
            if(to==pr||used[v])
                continue;
            dfs(to,v);
            sz[v]+=sz[to];
        }
    }

    int find_centroid(int v, int pr, int k){
        for(auto to:g[v]){
            if(to==pr||used[to])
                continue;
            if(sz[to]>=k/2)
                return find_centroid(to,v,k);
        }
        return v;
    }


    void build_centroid(int v, int parent){
        dfs(v,v);
        int centroid=find_centroid(v,v,sz[v]);
        used[centroid]=true;
        f[parent].push_back(centroid);
        for(auto to:g[centroid]){
            if(!used[to])
                build_centroid(to,centroid);
        }
    }
}

