void dfs(int v, int par = -1) {
    f[v] = 0;
    forn(i, g[v].size()) {
        int to = g[v][i];
        if (to == par) continue;
        dfs(to, v);
    }
    int d = 1 << 20;
    while (d) {
        int cnt = 0;
        forn(i, g[v].size()) {
            int to = g[v][i];
            if (to == par) continue;
            if (f[to] & d) cnt++;
        }
        if (cnt > 1) {
            f[v] += d << 1;
            return;
        }
        if (cnt == 1) f[v] |= d;
        d >>= 1;
    }
    f[v] += 1;
}

