#include <bits/stdc++.h>
using namespace std;

const int MAXN = 100005;

struct node {
	int to, next;
} edge[MAXN*2];
int head[MAXN], top = 0;
inline void push(int i, int j)
{ edge[++top] = (node) {j, head[i]}, head[i] = top; }

int n, a, b;
int siz[MAXN], vis[MAXN];

void dfs_siz(int nd, int f)
{
	siz[nd] = 1;
	for (int i = head[nd]; i; i = edge[i].next) {
		int to = edge[i].to;
		if (vis[to] || to == f) continue;
		dfs_siz(to, nd), siz[nd] += siz[to];
	}
}

void dfs_find(int nd, int f, const int totsiz, int &cur, int &ans)
{
	int maxsiz = totsiz-siz[nd];
	for (int i = head[nd]; i; i = edge[i].next) {
		int to = edge[i].to;
		if (vis[to] || to == f) continue;
		dfs_find(to, nd, totsiz, cur, ans);
		maxsiz = max(maxsiz, siz[to]);
	}
	if (maxsiz < cur) cur = maxsiz, ans = nd;
}

int dfs(int nd)
{
	dfs_siz(nd, 0);
	int tot = 1e9, cen;
	dfs_find(nd, 0, siz[nd], tot, cen), nd = cen;
	vis[nd] = 1;
	int ans = 0;
	for (int i = head[nd]; i; i = edge[i].next) {
		int to = edge[i].to;
		if (vis[to]) continue;
		ans = max(ans, dfs(to)+1);
	}
	return ans;
}

