	void init() {
		eid = 0;
		memset(head, -1, sizeof head);
	}
	void link( int u, int v ) {
		to[eid] = v, nxt[eid] = head[u], head[u] = eid++;
		to[eid] = u, nxt[eid] = head[v], head[v] = eid++;
	}
}

int n, f[N][M], g[N];

void dfs( int u, int fa ) {
	using namespace Tree;
	for( int i = head[u]; ~i; i = nxt[i] ) {
		int v = to[i];
		if( v == fa ) continue;
		dfs(v, u);
		for( int j = 0; j < M; ++j )
			f[u][j] += f[v][j];
	}
	int i;
	for( i = M-1; i; --i )
		if( f[u][i] >= 2 )
			break;
	while( f[u][i] ) ++i;
	g[u] = i, f[u][i] = 1;
	for( int j = 0; j < i; ++j )
		f[u][j] = 0;
}

