#include <bits/stdc++.h>
#define SZ(x) ((int) (x).size())
using namespace std;

typedef long long i64;

const int NMAX = 100005;

multiset<int> G[NMAX];
int chain[NMAX], diam[NMAX];

void dfs1(int node, int prev) {
    int vmax1 = 0, vmax2 = 0;
    for (int to: G[node]) {
        if (to == prev) {
            continue;
        }
        dfs1(to, node);
        if (vmax1 < chain[to]) {
            vmax2 = vmax1;
            vmax1 = chain[to];
        } else if (vmax2 < chain[to]) {
            vmax2 = chain[to];
        }
        diam[node] = max(diam[node], diam[to]);
    }
    diam[node] = vmax1 + vmax2 + 1;
    chain[node] = vmax1 + 1;
}

int maxDiam[NMAX];
int allMin;

int nextRoot = -1;

void dfs2(int node, int prev, int chainUp = 0, int diamUp = 0) {
    int vmax1 = chainUp, vmax2 = 0, vmax3 = 0;
    int dmax1 = diamUp, dmax2 = 0;
    maxDiam[node] = diamUp;
    for (int to: G[node]) {
        if (to == prev) {
            continue;
        }
        maxDiam[node] = max(maxDiam[node], diam[to]);
        if (chain[to] > vmax1) {
            vmax3 = vmax2;
            vmax2 = vmax1;
            vmax1 = chain[to];
        } else if (chain[to] > vmax2) {
            vmax3 = vmax2;
            vmax1 = chain[to];
        } else if (chain[to] > vmax3) {
            vmax3 = chain[to];
        }
        if (diam[to] > dmax1) {
            dmax2 = dmax1;
            dmax1 = diam[to];
        } else if (diam[to] > dmax2) {
            dmax2 = diam[to];
        }
    }
    if (maxDiam[node] < allMin) {
        nextRoot = node;
        allMin = maxDiam[node];
    }
    for (int to: G[node]) {
        if (to == prev) {
            continue;
        }
        int chainDown = (chain[to] == vmax1 ? vmax2: vmax1) + 1;
        int diamDown1;
        if (diam[to] == dmax1) {
            diamDown1 = dmax2;
        } else {
            diamDown1 = dmax1;
        }
        int diamDown2;
        if (chain[to] == vmax1) {
            diamDown2 = vmax2 + vmax3 + 1;
        } else {
            diamDown2 = vmax1 + vmax2 + 1;
        }
        int diamDown = max(diamDown1, diamDown2);
        dfs2(to, node, chainDown, diamDown);
    }
}

int solve(int root) {
    dfs1(root, -1);
    allMin = 0x3f3f3f3f;
    nextRoot = -1;
    dfs2(root, -1);
    root = nextRoot;
    int ans = 0;
    for (int to: G[root]) {
        G[to].erase(root);
        ans = max(ans, solve(to) + 1);
    }
    return ans;
}

