const int N=100000+19;
const int oo=(1<<30)-1;

struct Edge{
	int y,nxt;
} E[N*2];
int las[N],vis[N],fa[N],pre[N],suf[N],A[N],chk[N],len[N];
int n,cnt,mx,w,ans;

void Link(int x,int y){
	E[cnt]=(Edge){y,las[x]};las[x]=cnt++;
	E[cnt]=(Edge){x,las[y]};las[y]=cnt++;
}
void dfs(int x,int d){
	if (d>mx) mx=d,w=x;
	for (int i=las[x],y;~i;i=E[i].nxt)
		if (!vis[y=E[i].y]&&y!=fa[x]){
			fa[y]=x;
			dfs(y,d+1);
		}
}
int calc(int x,int fa){
	int res=0;
	for (int i=las[x],y;~i;i=E[i].nxt)
		if (!vis[y=E[i].y]&&!chk[y]&&y!=fa){
			res=max(res,calc(y,x)+1);
		}
	return res;
}
void solve(int x,int dep){
	ans=max(ans,dep);
	mx=-1;fa[x]=0;
	dfs(x,0);
	int u=w;
	mx=-1;fa[w]=0;
	dfs(w,0);
	*A=0;
	for (int i=w;i!=u;i=fa[i]) A[++*A]=i;
	A[++*A]=u;
	For(i,1,*A+1) chk[A[i]]=1;
	For(i,1,*A+1) len[i]=calc(A[i],-1);
	int tmp=0;
	pre[0]=suf[*A+1]=0;
	for (int i=1;i<=*A;i++){
		pre[i]=max(pre[i-1],len[i]+1+tmp);
		tmp=max(tmp+1,len[i]+1);
	}
	tmp=0;
	for (int i=*A;i>=1;i--){
		suf[i]=max(suf[i+1],len[i]+1+tmp);
		tmp=max(tmp+1,len[i]+1);
	}
	int mn=oo;
	For(i,1,*A+1){
		tmp=max(max(pre[i-1],suf[i+1]),len[i]);
		if (tmp<mn) mn=tmp,x=A[i];
		chk[A[i]]=0;
	}
	vis[x]=1;
	for (int i=las[x],y;~i;i=E[i].nxt)
		if (!vis[y=E[i].y]){
			solve(y,dep+1);
		}
}

