void Calculate_Depth(vector<vector<int>>&edge, vector<int>&depth, int stnode) {
	depth[stnode] = 0;
	queue<int>Q;
	Q.push(stnode);
	while (!Q.empty()) {
		int cn = Q.front();
		Q.pop();
		for (auto i : edge[cn]) {
			if (depth[i] > depth[cn] + 1) {
				depth[i] = depth[cn] + 1;
				Q.push(i);
			}
		}
	}
	return;
}

void Search(vector<vector<int>>&edge, vector<int>&num, vector<long long int>&dp, vector<int>&depth, int node = 0) {
	vector<int>fig(50);
	long long int bag = 0;
	for (auto i : edge[node]) {
		if (depth[i] < depth[node])continue;
		if (num[i] == -1)Search(edge, num, dp, depth, i);
		for (int j = 0; j < 50; j++) {
			fig[j] += (dp[i] >> j) & 1;
			bag |= dp[i];
		}
	}
	int nx = -1;
	for (int i = 0; i < 50; i++) {
		if (!fig[i]) {
			if (nx == -1)nx = i;
		}
		if (fig[i] >= 2)nx = -1;
	}
	num[node] = nx;
	dp[node] = ((bag >> nx) | 1) << nx;
	return;
}

