		R--;
		edge[L].push_back(R);
		edge[R].push_back(L);
	}
	vector<int>num(N, -1);
	vector<long long int>dp(N, -1);
	vector<int>depth(N, MOD);
	Calculate_Depth(edge, depth, 0);
	Search(edge, num, dp, depth);
	int ans = 0;
	for (auto i : num)ans = max(ans, i);
	cout << ans << endl;
	return 0;
}

