namespace nmy
{
	template<typename T>inline void check_max(T a,T &b){if(a>b)b=a;}
	template<typename T>inline void check_min(T a,T &b){if(a<b)b=a;}
	const int N=101000,M=N*2,K=20;
	int begin[N],next[M],to[M];
	int kjd[N],hdd[N];
	int n,e,ans;
	void add(int x,int y,bool k=1)
	{
		to[++e]=y;
		next[e]=begin[x];
		begin[x]=e;
		if(k)add(y,x,0);
	}
	void initialize()
	{
		scanf("%d",&n);
		for(int i=1,u,v;i<n;i++)
			scanf("%d%d",&u,&v),add(u,v);
	}
	void dfs(int p=1,int h=0)
	{
		int cnt[K+1];
		memset(cnt,0,sizeof(cnt));
		for(int i=begin[p],q;i;i=next[i])
			if((q=to[i])!=h)
			{
				dfs(q,p);
				for(int k=0;k<=K;k++)
					if(kjd[q]&(1<<k))cnt[k]++;

				for(int k=hdd[q];k<=K;k++)
					kjd[p]|=kjd[q]&(1<<k);
			}

		hdd[p]=K;
		for(int k=K;k>=0 && cnt[k]<=1;k--)
			if(!cnt[k])check_min(k,hdd[p]);
		kjd[p]|=1<<hdd[p];

		for(int k=0;k<hdd[p];k++)kjd[p]^=kjd[p]&(1<<k);

//		printf("f[%d] = %d\n",p,hdd[p]);

		check_max(hdd[p],ans);
	}
	void solve()
	{
		initialize();
		ans=0,dfs();
		printf("%d\n",ans);
	}
}
int main()
{
	nmy::solve();
	return 0;
}

