enum {
  MAXN = 100000 + 5
};

typedef std::vector<int> vi;

int n;
vi e[MAXN];
int deep[MAXN];

int dfs(int u, int pa) {
  int res(u), t;
  if (!~pa) deep[u] = 0;
  for (int v: e[u]) {
    if (v != pa) {
      deep[v] = deep[u]+1;
      t = dfs(v, u);
      if (deep[t] > deep[res])
	res = t;
    }
  }
  return res;
}

int calc(int x) {
  if (x == 1) return 1;
  return calc(x/2) + 1;
}

