vector < int > v[N];
int dfs(int node , int parent){
	int curor = 0;
	int two = 0;
	for(int next : v[node]){
		if(next == parent){
			continue;
		}
		int tmp = dfs(next , node);
		two |= tmp & curor;
		curor |= tmp;
	}
	two |= two >> 8;
	two |= two >> 4;
	two |= two >> 2;
	two |= two >> 1;
	int allowed = (~two) & (~curor);
	int me = allowed & -allowed;
	return (curor | me) & (~(me - 1));
}
