constexpr int maxn = 100000;
vector<int> G[maxn + 1];
int dp[maxn + 1];
int add(int u, int v){
    int res = 0;
    for(int k = 19; ~k; k -= 1){
        if(((u >> k) & 1) and ((v >> k) & 1)) return res + (1 << (k + 1));
        if(((u >> k) & 1) or ((v >> k) & 1)) res += 1 << k;
    }
    return res + 1;
}
void DFS(int u, int par = 0){
    vector<int> w;
    for(int v : G[u]) if(v != par){
        DFS(v, u);
        w.push_back(dp[v]);       
    }
    if(w.empty()) dp[u] = 1;
    else if(w.size() == 1u) dp[u] = w[0] + 1;
    else{
        sort(w.begin(), w.end(), greater<int>());
        dp[u] = add(w[0], w[1]);
    }
}
