inline void dfs(int b,int pr=-1){
	over[b] = -1;
	dp[b].clear();
	for (int i = 0; i < v[b].size(); i++){
		if (v[b][i] == pr)continue;
		dfs(v[b][i],b);
		if (dp[b].size() < dp[v[b][i]].size()){
			swap(dp[b], dp[v[b][i]]);
		}
		for (int i2 : dp[v[b][i]]){
			if (dp[b].count(i2)){
				over[b] = max(over[b], i2);
			}
			dp[b].insert(i2);
		}
		dp[v[b][i]].clear();
	}
	auto it = dp[b].begin();
	int ex = 0;
	while (it != dp[b].end()){
		if (ex == (*it)){
			it = dp[b].erase(it);
			ex++;
			continue;
		}
		else{
			if ((*it) <= over[b]){
				it = dp[b].erase(it);
				ex = over[b]+1;
				continue;
			}
			else{
				dp[b].insert(ex);
				return;
			}
		}
	}
	dp[b].insert(ex);
}

