const int MAXN = 100005;
int last_vert, cd[MAXN];
vector <int> vis;
set <int> G[MAXN];
void dfs2(int pos, int par)
{
	last_vert = pos;
	vis.push_back(pos);
	for (int v: G[pos])
	{
		if(v != par)
		{
			cd[v] = 1 + cd[pos];
			dfs2(v,pos);
		}
	}
}
int dfs1(int pos)
{
	cd[pos] = 0;
	vis.clear();
	dfs2(pos,-1);
	cd[last_vert] = 0;
	vis.clear();
	dfs2(last_vert,-1);
	vector <int> dia;
	for (int i = 0; i < vis.size(); ++i)
	{
		if(cd[vis[i]] == (cd[last_vert]/2) || cd[vis[i]] == (cd[last_vert] + 1)/2)
			dia.push_back(vis[i]);
	}
	if(dia.size() == 2)
	{
		int u = dia[0], v = dia[1];
		G[u].erase(v);
		G[v].erase(u);
		// cout<<"break "<<u<<" "<<v<<"\n";
		return (max(dfs1(u),dfs1(v)) + 1);
	}
	else
	{
		int u = dia[0], ans = 0;
		// cout<<"remove "<<u<<"\n";
		for (set <int>::iterator it = G[u].begin(); it != G[u].end(); ++it)
		{
			G[*it].erase(u);
			ans = max(ans, 1 + dfs1(*it));
		}
		return ans;
	}
}
