const int N = 2e5 + 7;
const int LOG = 25;

int n;
vector<int> adj[N];
int ans;

int dfs(int u, int from) {
	int all = 0, least = 0;
	for (int v : adj[u]) {
		if (v == from) continue;
		int mask = dfs(v, u);
		for (int j = 0; j < LOG; j++) {
			if ((all >> j & 1) && (mask >> j & 1)) {
				least = j + 1;
			}
		}
		all |= mask;
	}
	for (int i = least; ; i++) {
		if (~all >> i & 1) {
			ans = max(ans, i);
			all ^= (1 << i);
			for (int j = i - 1; j >= 0; j--) {
				if (all >> j & 1) {
					all ^= (1 << j);
				}
			}
			return all;
		}
	}
}

