typedef pair<int, int> pii;
const int N = 1e5 + 100, inF = N;

int n, u, v, ans = inF, down[N], up[N];
bool mark[N];
vector<int> nei[N];

void find_down(int v, vector<int> *vec, int par = -1) {
	(*vec).push_back(v);
	for (int u: nei[v])
		if(u != par && mark[u] == false) {
			find_down(u, vec, v);
			down[v] = max(down[v], down[u] + 1);
		}
}

void find_up(int v, int par = -1) {
	pii mx1 = pii(up[v] + 1, v), mx2 = pii(-inF, -inF);
	for (int u: nei[v])
		if(u != par && mark[u] == false) {
			mx2 = max(mx2, pii(down[u] + 2, u));
			if(mx2 > mx1)
				swap(mx2, mx1);
		}
	for (int u: nei[v])
		if(u != par && mark[u] == false) {
			if(u != mx1.second)
				up[u] = mx1.first;
			else
				up[u] = mx2.first;
			find_up(u, v);
		}
}

int solve(int v) {
	vector<int> vec;
	find_down(v, &vec);
	find_up(v);
	int mini = inF, cen = v;
	for (int u: vec)
		if(max(up[u], down[u]) < mini) {
			mini = max(up[u], down[u]);
			cen = u;
		}
	for (int u: vec)
		up[u] = down[u] = 0;
	mark[cen] = true;
	int maxi = -1;
	for (int u: nei[cen])
		if(mark[u] == false)
			maxi = max(maxi, solve(u));
	return maxi + 1;
}

