int solve(int u, int prev = -1)
{
	int cnt[maxlg] = {0}, res = 0;
	for (int i : graph[u])
		if (i != prev)
		{
			int val = solve(i, u);
			for (int j = 0; j < maxlg; j++)
				cnt[j] += ((val >> j) & 1);
		}

	int pos = -1;
	for (int i = 0; i < maxlg; i++)
		if (cnt[i] > 1)
			pos = i;

	for (int i = pos + 1; i < maxlg; i++)
		if (!cnt[i])
		{
			res += (1 << i);
			for (int j = i; j < maxlg; j++)
				if (cnt[j])
					res += (1 << j);
			break;
		}

	return res;
}

int highBit(int mask)
{
	for (int i = maxlg - 1; i >= 0; i--)
		if ((mask >> i) & 1)
			return i;
	return -1;
}

