void AddEdge(int u,int v) {
	to[++e]=v;nxt[e]=first[u];first[u]=e;
	to[++e]=u;nxt[e]=first[v];first[v]=e;
}
int f[maxn],siz[maxn],vis[maxn],size,root;
void getroot(int x,int fa) {
	int maxs=0;siz[x]=1;
	for(int i=first[x];i;i=nxt[i]) if(to[i]!=fa&&!vis[to[i]]) {
		getroot(to[i],x);siz[x]+=siz[to[i]];
		maxs=max(maxs,siz[to[i]]);
	}
	f[x]=max(maxs,size-siz[x]);
	if(f[root]>f[x]) root=x;
}
void dfs(int x,int fa) {
	siz[x]=1;
	for(int i=first[x];i;i=nxt[i]) if(to[i]!=fa&&!vis[to[i]]) dfs(to[i],x),siz[x]+=siz[to[i]];
}
