	ans=max(ans,dep);vis[x]=1;
	for(int i=first[x];i;i=nxt[i]) if(!vis[to[i]]) {
		dfs(to[i],x);
		f[0]=size=siz[to[i]];getroot(to[i],root=0);
		solve(root,dep+1);
	}
}
