	rep(i, 0, N) {
		if(cnt[i] == 1) que.push(i);
	}
	int ans = 0;
	while(!que.empty()) {
		int v = que.front(); que.pop();
		int at = 0;
		rep(i, 0, sz(G[v])) {
			int n = G[v][i];
			if(cnt[n] != 0) continue;
			if(sz(S[n]) > sz(S[v])) swap(S[n], S[v]);
