				if(S[v].count(d)) {
					while((*(S[v].begin())) < d) S[v].erase(S[v].begin());
					at = d;
				}
				else if(d > at) S[v].insert(d);
			}
		}
		while(!S[v].empty() && (*(S[v].begin())) == at) {
			S[v].erase(S[v].begin());
			at++;
		}
		S[v].insert(at);
		MAX(ans, at);

