void dfs(int x,int p){
	int maxf=0;
	static int tr[MAXD];
	memset(tr,0,sizeof tr);
	for(int i=g[x];~i;i=e[i].next)
		if(e[i].to^p){
			dfs(e[i].to,x);
			if(f[e[i].to]>maxf)
				maxf=f[e[i].to];
			if(tr[f[e[i].to]]) tr[f[e[i].to]]=-1;
			else tr[f[e[i].to]]=h[e[i].to];
		}
	if(!maxf){
		f[x]=h[x]=1;
		return;
	}
	int now=maxf;
	bool flag=0;
	while(tr[now]){
		if(tr[now]==-1){
			flag=1;
			break;
		}
		int temp=tr[now];
		while((--now)>=temp)
			if(tr[now]){
				flag=1;
				break;
			}
		if(flag) break;
	}
	if(!flag){
		f[x]=maxf;
		h[x]=now;
	}else{
		f[x]=h[x]=maxf+1;
	}
}

