#include <bits/stdc++.h>

using namespace std;

int Parent[500000], Dist[500000];
set<int> G[500000];

int DFS(int node, int par, int dist) {
	Parent[node] = par;
	Dist[node] = dist;

	int ret = node;
	for(auto vec : G[node])
		if(vec != par) 
			ret = max(ret, DFS(vec, node, dist + 1), [&](int a, int b) {
				return Dist[a] < Dist[b]; 
			});

	return ret;
}

int GetCenter(int node) {
	int last = DFS(node, 0, 0);
	last = DFS(last, 0, 0);

	int d = Dist[last];
	for(int i = 0; i < d / 2; ++i)
		last = Parent[last];
	if(d % 2 == 1 && rand() % 2)
		last = Parent[last];

	int ret = last;

	last = DFS(last, 0, 0);
	assert(Dist[last] == (d + 1) / 2);

	return ret;
}

int GetDiam(int node) {
	int last = DFS(node, 0, 0);
	last = DFS(last, 0, 0);
	return Dist[last];
}

int Decomp(int node) {
	int cen = GetCenter(node);
	for(auto vec : G[cen]) {
		G[vec].erase(cen);
	}

	int ans = 0;
	for(auto vec : G[cen])
		ans = max(ans, Decomp(vec) + 1);
	return ans;
}

int GetDP(int n) {
	if(n == 1) return 0;
	return GetDP(n / 2) + 1;
}

