void dfs ( int v , int p = -1 ) {
	sz [ v ] = 1 ;
	for ( int u : adj [ v ] ) {
		if ( u == p ) continue ;
		if ( !mark [ u ] ) {
			dfs ( u , v ) ;
			sz [ v ] += sz [ u ] ; 
		}
	}
}

int get_centroid ( int v , int p = -1 ) {
	for ( int u : adj [ v ] ) {
		if ( mark [ u ] ) continue ;
		if ( sz [ u ] > all / 2 && u != p )
			return get_centroid ( u , v ) ;  
	}
	return v ; 
}

