template <typename E=Edge>
class Tree : public Graph<E> {
    private:
    using Graph<E>::add_edge;
    void build(Graph<E>& g, int v,int p) {
        for(int edge_id:g[v]){
            auto e = g.i2e(edge_id);
            const int u = e.versus(v);
            if(p==u)continue;
            //add_arc(e);
            this->add_arc(v,u);
            build(g,u,v);
        }
    }
    public:
    const int root;
    Tree(int reserved_vertex_size = 1, int reserved_edge_size = -1, const int root = 0)
    : Graph<E>(reserved_vertex_size, reserved_edge_size) , root(root) {}
    Tree(Graph<E>& g, const int root = 0)
    : Graph<E>(g.size(), g.size()-1), root(root){
        build(g,root,root);
    }
};

using X = array<int,32>;
X dfs(Tree<>& tree, int v){
    X ret;
    for(int i:range(32))ret[i]=0;
    for(int u:tree.Ns(v)){
        X cld = dfs(tree,u);
        for(int i:range(32))ret[i]+=cld[i];
    }
    int low = -1;
    for(int i:range(32)){
        if(ret[i]>=2)low = i;
    }
    int up = low+1;
    while(ret[up]>0){
        up++;
    }
    for(int i:range(up)){
        ret[i]=0;
    }
    ret[up]++;
    //cerr << v << " " << up << " ";
    //for(int i:range(32))cerr<<ret[i];
