const int MAXN = 100005;

vector<int> T[MAXN];
int dad[MAXN];
bool alive[MAXN];
int L[MAXN];
int R[MAXN];

pair<int,int> far_dfs(int i, int d = 0, int cur_dad = -1) {
  pair<int,int> ret = {d, i};
  dad[i] = cur_dad;
  for (int j : T[i])
    if (alive[j] && j != cur_dad)
      ret = max(ret, far_dfs(j, d + 1, i));
  return ret;
}

int depth(int i, int dad) {
  int ret = 0;

  for (int j : T[i]) {
    if (alive[j] && j == dad) continue;
    ret = max(ret, 1 + depth(j, i));
  }

  return ret;
}

int solve(int i) {
  auto a = far_dfs(i);
  auto b = far_dfs(a.second);

  if (b.first <= 1)
    return b.first;

  vector<int> path;

  for (int i = b.second; i != -1; i = dad[i]) 
    path.push_back(i);

  int cd = 0;
  for (int i = 0; i < (int)path.size() - 1; ++i) {
    for (int j : T[path[i]]) {
      if (!alive[j] || j == path[i + 1] || i && j == path[i - 1]) continue;
      cd = max(cd, depth(j, path[i]) + 1);
    }
    L[i] = cd;
    ++cd;
  }

  cd = 0;
  for (int i = (int)path.size() - 1; i >= 1; --i) {
    for (int j : T[path[i]]) {
      if (!alive[j] || j == path[i - 1]
	  || i + 1 < (int)path.size() && j == path[i + 1]) continue;
      cd = max(cd, depth(j, path[i]) + 1);
    }
    R[i] = cd;
    ++cd;
  }

  int best = 1;
  int best_len = 1e9;
  for (int i = 1; i < (int)path.size() - 1; ++i) {
    if (max(L[i - 1], R[i + 1]) < best_len) {
      best_len = max(L[i - 1], R[i + 1]);
      best = i;
    }
  }

  alive[path[best]] = 0;

  return 1 + max(solve(path[best - 1]), solve(path[best + 1]));
}

int main(void) 
{
  int N;
  scanf("%d", &N);

  FOR(i, 1, N + 1) alive[i] = 1;

  FOR(i, 1, N) {
    int a, b;
    scanf("%d%d", &a, &b);
    T[a].push_back(b);
    T[b].push_back(a);
  }

  printf("%d\n", solve(1));

  return 0;
}

