#define INT long long
#define MOD 1000000007
using namespace std;
INT n, d;
vector<INT> g[100010];
int solve(int x)
{
    int chk[100010] = {0};
    int ret = x;
    queue<INT> q;
    q.push(x), chk[x] = 1;
    while( !q.empty() )
    {
        int v = q.front(); q.pop();
        ret = v;
        for( auto w : g[v] )
            if( !chk[w] )
                q.push(w), chk[w] = chk[v]+1;
    }
    return ret;
}
int f(int x, int y)
{
    int chk[100010] = {0};
    int ret = 0;
    queue<INT> q;
    q.push(x), chk[x] = 1;
    while( !q.empty() )
    {
        int v = q.front(); q.pop();
        if( v == y ) return chk[v]-1;
        for( auto w : g[v] )
            if( !chk[w] )
                q.push(w), chk[w] = chk[v]+1;
    }
    return 0;
}
int main()
{
    ios::sync_with_stdio(false);
    cin>>n;
    for(int i = 1 ; i < n ; i++ )
    {
        INT a, b;
        cin>>a>>b;
        g[a].push_back(b);
        g[b].push_back(a);
    }
    int v = solve(1);
    int w = solve(v);
    int d = f(v, w);
    cout<<(d+1)/2-1<<endl;
    return 0;
}

