using namespace std;
const int K=20;
const int N=1e6;
int n,head[N+10],tot=0;
struct data {
    int next,num;
}edge[N+10];
void Add(int u,int v) {
    edge[++tot].next=head[u];
    edge[tot].num=v;
    head[u]=tot;
}
int dfs(int x,int fat) {
    int nowx=0,maxbit=-1;
    for (int i=head[x];i!=-1;i=edge[i].next) {
        int kx=edge[i].num;
        if (kx==fat) continue;
        int nowv=dfs(kx,x);
        for (int j=0;j<=K;j++)
            if ((nowv&(1<<j))&&(nowx&(1<<j)))
                maxbit=max(maxbit,j);
        nowx|=nowv;
    }
    for (int j=0;j<=maxbit;j++)
        if (nowx&(1<<j)) nowx^=(1<<j);
    for (int j=maxbit+1;j<=K;j++)
        if ((nowx&(1<<j))==0) {
            nowx|=(1<<j);
            return nowx;
        }
        else nowx^=(1<<j);
    return 233;
}
int main() {
    memset(head,-1,sizeof(head));
    scanf("%d",&n);
    for (int i=1;i<n;i++) {
        int u,v;
        scanf("%d%d",&u,&v);
        Add(u,v);
        Add(v,u);
    }
    int ans=dfs(1,-1);
    for (int i=K;i>=0;i--)
        if (ans&(1<<i)) {
            printf("%d\n",i);
            return 0;
        }
    return 0;
}

