	for(;ch>='0' && ch<='9';ch=getchar())q=q*10+ch-48;n=q*w;return n;
}
int m,n,ans;
int B[2*N][2],A[N],B0;
int dp[N];
bool z[N];
void link(int q,int w)
{
	B[++B0][0]=A[q],A[q]=B0,B[B0][1]=w;
	B[++B0][0]=A[w],A[w]=B0,B[B0][1]=q;
}
void dfsf(int q,int c,int fa)
{
	dp[q]=1;
	efo(i,q)if(B[i][1]!=fa&&!z[B[i][1]])dfsf(B[i][1],c+1,q),dp[q]=max(dp[q],dp[B[i][1]]+1);
}
int zx,zx1;
void dfs(int q,int mx,int fa)
{
	int mx1=0,mxz=0;
	efo(i,q)if(B[i][1]!=fa&&!z[B[i][1]])
	{
		if(dp[B[i][1]]>mx)mx1=mx,mx=dp[B[i][1]],mxz=B[i][1];
		else if(dp[B[i][1]]>mx1)mx1=dp[B[i][1]];
	}
	if(zx1>mx)zx1=mx,zx=q;
	efo(i,q)if(B[i][1]!=fa&&!z[B[i][1]])dfs(B[i][1],(mxz==B[i][1]?mx1:mx),q);
}
void divide(int q,int ansc)
{
	ans=max(ans,ansc);
	dfsf(q,1,0);zx1=1e9;
	dfs(q,0,0);q=zx;
	z[q]=1;//printf("%d\n",q);
	efo(i,q)if(!z[B[i][1]])divide(B[i][1],ansc+1);
}
int main()
{
	int q,w;
	read(n);
	fo(i,1,n-1)read(q),read(w),link(q,w);
	divide(1,0);
	printf("%d\n",ans-1);
	return 0;
}

