	}
	return;
}
int dfs(int current, int parent)
{
	int value=1;
	visited[current]=true;
	for(int next : graph[current])
	{
		if(!visited[next])
		{
			int firstResult=dfs(next, current);
			value+=firstResult;
		}
	}
	dp[current]=value;
	return value;
}
