int main() {
    // input
    int n; scanf("%d", &n);
    vector<vector<int> > g(n);
    repeat (i, n - 1) {
        int a, b; scanf("%d%d", &a, &b); -- a; -- b;
        g[a].push_back(b);
        g[b].push_back(a);
    }
    // solve
    function<int (int, int)> go = [&](int i, int parent) {
        vector<int> qs;
        int sum_q = 0;
        for (int j : g[i]) if (j != parent) {
            int q = go(j, i);
            qs.push_back(q);
            sum_q |= q;
        }
        int sum_and_q = 0;
        repeat (x, qs.size()) repeat (y, x) sum_and_q |= qs[x] & qs[y];
        int p = 1;
        while (p <= sum_and_q or sum_q & p) p <<= 1;
        return (sum_q & ~ (p - 1)) | p;
    };
    int p = go(0, -1);
    int k = 31 - __builtin_clz(p);
    // output
    printf("%d\n", k);
    return 0;
}

