void build(int a, int p) {
    for (int i = 0;i < G[a].size();i++) {
        if (G[a][i] == p) continue;
        T[a].push_back(G[a][i]);
        build(G[a][i], a);
    }
    return;
}

int cal(int n) {
    if (V[n] != 0) return V[n];
    if (T[n].size() == 0) return 1;
    int v = 0;
    for (int i = 0;i < T[n].size();i++) {
        v = (v | cal(T[n][i]));
    }
    int x = 1;
    for (int i = 0;i < 31;i++) {
        if ((x & v) == 0) {
            int num = (x | v);
            int y = 1;
            for (int j = 0;j < i;j++) {
                if (num & y) num -= y;
                y *= 2;
            }
            V[n] = num;
            return num;
        }
        x *= 2;
    }
    return 0;
}

