int size_of_subtree(int v, vector<bool>& used, int p) {
    int ret = 1;
    for (int i = 0; i < G[v].size(); i++) {
        int cur = G[v][i];
        if (centroid[cur] || used[cur] || cur == p)continue;
        ret += size_of_subtree(cur, used, v);
    }
    return sos[v] = ret;
}

int search_centroid(int v, int t, vector<bool>& used) {
    if (used[v])return -1;
    int p = -1;
    while (true) {
        int ch, s = 0;
        for (int i = 0; i < G[v].size(); i++) {
            int cur = G[v][i];
            if (centroid[cur] || used[cur] || cur == p)continue;
            if (s <= sos[cur])s = sos[cur], ch = cur;
        }
        if (s <= t / 2)break;
        p = v;
        v = ch;
    }
    return v;
}

int u(int v) {
    vector<bool> used(N);
    int ret = LLONG_MAX;
    while (true) {
        size_of_subtree(v, used, -1);
        int cidx = search_centroid(v, sos[v], used);
        if (cidx == -1)return ret;
        centroid[cidx] = true;
        used[cidx] = true;
        int mu = -1, m2 = -1;
        for (int i = 0; i < G[cidx].size(); i++) {
            if (centroid[G[cidx][i]])continue;
            int cu = u(G[cidx][i]);

            if (mu <= cu)m2 = mu, mu = cu, v = G[cidx][i];
        }
        ret = min(ret, mu + 1);
        centroid[cidx] = false;
        if (mu == m2) return ret;
    }
}

