vector<int> strategy_function(const vector<vector<int>> &g)
{
	vector<int> label(g.size());
	function<int(int)> dfs = [&](int u)
	{
		label[u] = -1;
		int used = 0, more = 0;
		for (int v : g[u])
			if (!label[v])
			{
				int mask = dfs(v);
				more |= mask & used;
				used |= mask;
			}
		for (int k : {1, 2, 4, 8, 16})
			more |= more >> k;
		++more;
		int possible = ~used & -more;
		label[u] = possible & -possible;
		used |= label[u];
		used &= -label[u];
		return used;
	};
	dfs(0);
	return label;
}

