pair<int,int> dfs_center(int cur,int pre) {
	pair<int,int> res=make_pair(1<<30,-1);
	int ma=0;
	num[cur]=1;

	FORR(r,E[cur]) if(r!=pre) {
		res=min(res,dfs_center(r,cur));
		ma=max(ma,num[r]);
		num[cur]+=num[r];
	}
	return min(res,make_pair(max(ma,N-num[cur]),cur));
}

int dfs(int cur,int pre) {
	int cnt[32]={};
	int mi=-1;
	int mask=0,i;
	FORR(r,E[cur]) if(r!=pre) {
		int ret = dfs(r,cur);
		mask |= ret;
		FOR(i,30) {
			if(mask&(1<<i)) cnt[i]++;
			if(cnt[i]>=2) mi=max(mi,i);
		}
	}
	mi++;
	while(cnt[mi]) mi++;
	mask |= 1<<(mi);
	FOR(i,mi) if(mask&(1<<i)) mask ^= 1<<i;
	return mask;
}

