		if (a[pos][i] != fa) {
			work(a[pos][i], pos);
			for (int j = 0; j < MAXLOG; j++)
				ans[pos][j] += ans[a[pos][i]][j];
		}
	int home = -1;
	for (int i = MAXLOG - 1; i >= 0; i--)
		if (ans[pos][i] >= 2) {
			home = i;
			break;
		}
	for (int i = 0; i <= home; i++)
		ans[pos][i] = 0;
	for (int i = home + 1; i < MAXLOG; i++) {
		ans[pos][i]++;
		if (ans[pos][i] == 1) break;
		else ans[pos][i] = 0;
	}
}
